import React, {useState, useEffect} from 'react'
import axios from 'axios'

const EmployeesList = () => {
    const [employees, setEmployees] = useState([])

    useEffect(() => {
        const fetchAllEmployees = async ()=> {
            try{
                const res = await axios.get("http://localhost:6197/employeeslist")
                setEmployees(res.data)
            } catch(err){
                console.log(err)
            }
        };
        fetchAllEmployees();
    }, []);

    const handleDelete = async (user_id) => {
        try {
            await axios.delete("http://localhost:6197/employeeslist" + user_id)
            window.location.reload()
        } catch(err){
            console.log(err)
        }
    }


    return (

        <div>
            <h1>Table of Employees</h1>

            <div className="users-div">
                <table className="table-zebra">
                    <tr className="user-row">
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Middle Name</th>
                        <th>Last Name</th>
                        <th>Work Email</th>
                        <th>Contact Num</th>
                        <th>Sex</th>
                        <th>Operations</th>
                    </tr>

                    { employees.map((employee) => (
                        <tr className="user-row">
                            <td>{employee.emp_id}</td>
                            <td>{employee.f_name}</td>
                            <td>{employee.m_name}</td>
                            <td>{employee.s_name}</td>
                            <td>{employee.work_email}</td>
                            <td>{employee.contact_num}</td>
                            <td>{employee.sex}</td>
                            <td>
                                <button>Update</button>
                                <button onClick={ handleDelete(employee.user_id) }>Delete</button>
                            </td>
                        </tr>
                    ))}
                </table>
            </div>

        </div>
    )
}

export default EmployeesList
